import org.apache.nifi.flowfile.FlowFile
import org.apache.nifi.processor.io.StreamCallback
import org.apache.nifi.processor.exception.ProcessException

import groovy.json.JsonSlurper
import org.apache.commons.codec.binary.Base64
import java.net.URLDecoder
import java.nio.charset.StandardCharsets

// Step 1: Get flowfile
FlowFile flowFile = session.get()
if (!flowFile) return

try {
    // Step 2: Get input attributes
    String accessToken = flowFile.getAttribute("access_token")
    String folderLink = flowFile.getAttribute("folder_link")

    if (!accessToken || !folderLink) {
        flowFile = session.putAttribute(flowFile, "error", "Missing access_token or folder_link")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Step 3: Encode the SharePoint URL (Base64 URL-safe for Graph API)
    String base64Encoded = Base64.encodeBase64String(folderLink.getBytes(StandardCharsets.UTF_8))
    String encodedUrl = base64Encoded
        .replace("/", "_")
        .replace("+", "-")
        .replaceAll("=+\$", "")  // Trim trailing equals signs

    // Step 4: Resolve the folder driveItem via Graph API
    String apiUrl = "https://graph.microsoft.com/v1.0/shares/u!${encodedUrl}/driveItem"
    def connection = new URL(apiUrl).openConnection()
    connection.setRequestMethod("GET")
    connection.setRequestProperty("Authorization", "Bearer " + accessToken)
    connection.setRequestProperty("Accept", "application/json")

    int responseCode = connection.getResponseCode()
    if (responseCode != 200) {
        flowFile = session.putAttribute(flowFile, "error", "Graph API call failed with status ${responseCode}")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Step 5: Parse the JSON response
    def json = new JsonSlurper().parse(connection.getInputStream())

    def driveId = json?.parentReference?.driveId
    def itemId = json?.id
    def folderName = json?.name

    if (!driveId || !itemId) {
        flowFile = session.putAttribute(flowFile, "error", "Missing driveId or itemId in API response")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Step 6: Set attributes for downstream use
    flowFile = session.putAttribute(flowFile, "driveId", driveId)
    flowFile = session.putAttribute(flowFile, "itemId", itemId)
    flowFile = session.putAttribute(flowFile, "folder_name", folderName)

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Error resolving SharePoint folder link", e)
    session.transfer(flowFile, REL_FAILURE)
}

import org.apache.nifi.flowfile.FlowFile
import org.apache.nifi.processor.io.StreamCallback
import org.apache.commons.codec.binary.Base64
import java.net.URLDecoder
import java.nio.charset.StandardCharsets

FlowFile flowFile = session.get()
if (!flowFile) return

try {
    // Get the SharePoint resume link from attribute
    String resumeLink = flowFile.getAttribute("resume_link")
    if (!resumeLink) {
        log.error("Missing 'resume_link' attribute in flowfile.")
        session.transfer(flowFile, REL_FAILURE)
        return
    }

    // Encode link into Graph API-safe base64
    String base64Encoded = Base64.encodeBase64String(resumeLink.getBytes(StandardCharsets.UTF_8))
    String graphSafe = base64Encoded
        .replace("/", "_")
        .replace("+", "-")
        .replaceAll("=+\$", "")  // Remove trailing '=' safely

    // Extract filename from URL (look for 'file=' param or last path component)
    String filename = null
    def matcher = resumeLink =~ /[?&]file=([^&]+)/
    if (matcher.find()) {
        filename = URLDecoder.decode(matcher.group(1), "UTF-8")
    } else {
        // Fallback to last path segment after last slash
        def parts = resumeLink.split("/")
        filename = URLDecoder.decode(parts[-1], "UTF-8")
    }

    // Set encoded and filename attributes
    flowFile = session.putAttribute(flowFile, "sharepoint.encoded", graphSafe)
    flowFile = session.putAttribute(flowFile, "sharepoint.filename", filename)
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Failed processing SharePoint link: ", e)
    session.transfer(flowFile, REL_FAILURE)
}

