import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

flowFile = session.get()
if(!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def reader = new BufferedReader(new InputStreamReader(inputStream, StandardCharsets.UTF_8))
    def slurper = new JsonSlurper()
    def results = []

    reader.eachLine { line ->
        if (!line.trim()) return  // skip empty lines
        def resume = slurper.parseText(line)

        def keywords = (resume.keywords ?: []).collect { it.toLowerCase() }

        def textParts = []
        textParts << (resume.summary ?: "")
        textParts.addAll(resume.skills ?: [])

        (resume.projects ?: []).each { proj ->
            textParts << (proj.title ?: "")
            textParts << (proj.description ?: "")
        }

        (resume.certifications ?: []).each { cert ->
            textParts << (cert.title ?: "")
            textParts << (cert.issuer ?: "")
        }

        (resume.education ?: []).each { edu ->
            textParts << (edu.degree ?: "")
            textParts << (edu.institution ?: "")
        }

        textParts.addAll(resume.languages ?: [])

        def combinedText = textParts.join(" ").toLowerCase()

        def matched = keywords.any { kw -> combinedText.contains(kw) }

        resume.matched = matched
        results << resume
    }

    def outputJson = JsonOutput.toJson(results)
    outputStream.write(outputJson.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

