import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import org.apache.nifi.processor.io.*
import java.nio.charset.StandardCharsets

// Recursive flattener
def flattenMap(map, prefix = '', collector = []) {
    map.each { k, v ->
        def key = prefix ? "${prefix}.${k}" : "${k}"
        if (v instanceof Map) {
            flattenMap(v, key, collector)
        } else if (v instanceof List) {
            v.eachWithIndex { item, i ->
                flattenMap([(i): item], key, collector)
            }
        } else {
            collector << "${key}=${v}"
        }
    }
    return collector
}

