import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import org.apache.commons.io.IOUtils
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def input = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    def json = new JsonSlurper().parseText(input)

    // Extract embedding array
    def embeddingArray = json["embedding"]

    // Create output JSON
    def outputJson = [query_vector: embeddingArray]
    outputStream.write(JsonOutput.toJson(outputJson).getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

