import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

try {
    // Extract employee_id from filename (e.g., "123456.json" → "123456")
    def filename = flowFile.getAttribute("filename")
    def employeeId = filename.replaceFirst(/\.[^.]+$/, "")

    // Azure OpenAI configuration
    def API_KEY = ""
    def ENDPOINT = "https://us-tax-law-rag-demo.openai.azure.com"
    def DEPLOYMENT = "gpt-4o-mini"
    def API_VERSION = "2024-08-01-preview"

    // Read content from FlowFile
    def contentStream = session.read(flowFile)
    def inputJson = new JsonSlurper().parseText(IOUtils.toString(contentStream, StandardCharsets.UTF_8))
    contentStream.close()

    def eid = inputJson.employee_id ?: employeeId
    def name = inputJson.employee_name ?: ""
    def newSkills = (inputJson.new_data?.skills ?: []).join(", ")
    def newProjects = inputJson.new_data?.projects ?: ""
    def oldSkills = (inputJson.existing_data?.skills ?: []).join(", ")
    def oldProjects = inputJson.existing_data?.projects ?: ""

    // Prompt to LLM
    def prompt = """
You are a resume standardization assistant. You are given both new and existing resume data for a candidate.

Your task is to produce a complete, clean, and structured JSON resume in the following strict format:

{
  "employee_id": str,
  "name": str,
  "email": str,
  "phone": str,
  "location": str,
  "summary": str,
  "education": [
    {
      "degree": str,
      "institution": str,
      "year": int or str
    }
  ],
  "experience": [
    {
      "title": str,
      "company": str,
      "duration": str,
      "location": str (optional),
      "description": str
    }
  ],
  "skills": [str],
  "projects": [
    {
      "title": str,
      "description": str,
      "link": str (optional)
    }
  ],
  "certifications": [
    {
      "title": str,
      "issuer": str (optional),
      "year": str or int (optional),
      "link": str (optional)
    }
  ],
  "languages": [str],
  "social_profiles": [
    {
      "platform": str,
      "link": str
    }
  ]
}

--- INPUT ---
Employee ID: ${eid}
Name: ${name}

--- New Skills ---
${newSkills}

--- New Projects ---
${newProjects}

--- Existing Skills ---
${oldSkills}

--- Existing Projects ---
${oldProjects}

--- INSTRUCTIONS ---
- Use both new and existing data.
- Remove duplicates.
- Normalize structure.
- Output only a valid JSON object in the exact format above.
- Do not wrap in markdown or add explanations.
"""

    // Build the OpenAI request
    def requestBody = JsonOutput.toJson([
        messages: [
            [role: "system", content: "You are a helpful assistant that formats resumes into structured JSON."],
            [role: "user", content: prompt]
        ],
        temperature: 0.2,
        max_tokens: 6000
    ])

    def conn = new URL("${ENDPOINT}/openai/deployments/${DEPLOYMENT}/chat/completions?api-version=${API_VERSION}")
        .openConnection()
    conn.setRequestMethod("POST")
    conn.setDoOutput(true)
    conn.setRequestProperty("Content-Type", "application/json")
    conn.setRequestProperty("api-key", API_KEY)

    conn.outputStream.withWriter("UTF-8") { it.write(requestBody) }

    def responseText = conn.inputStream.withReader("UTF-8") { it.text }
    def parsed = new JsonSlurper().parseText(responseText)

    def rawLLM = parsed.choices[0].message.content?.trim()
    def cleaned = rawLLM?.replaceAll(/(?s)```(json)?/, "")?.trim()
    def standardized = new JsonSlurper().parseText(cleaned)

    // Inject employee_id (in case it's missing)
    standardized.employee_id = eid

    def finalJson = JsonOutput.prettyPrint(JsonOutput.toJson(standardized))

    // Write to FlowFile
    flowFile = session.write(flowFile, { out ->
        out.write(finalJson.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)

    flowFile = session.putAttribute(flowFile, "employee_id", eid)

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("❌ Error during standardization: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}

import org.apache.nifi.flowfile.FlowFile
import org.apache.nifi.processor.io.StreamCallback
import org.apache.nifi.processor.exception.ProcessException

import groovy.json.JsonSlurper
import java.nio.charset.StandardCharsets

def flowFile = session.get()
if (!flowFile) return

def accessToken = flowFile.getAttribute("access_token")
def encoded = flowFile.getAttribute("sharepoint.encoded")

if (!accessToken || !encoded) {
    flowFile = session.putAttribute(flowFile, "error", "Missing access_token or sharepoint.encoded")
    session.transfer(flowFile, REL_FAILURE)
    return
}

// ✅ Important: prepend "u!" to the encoded string
def apiUrl = "https://graph.microsoft.com/v1.0/shares/u!${encoded}/driveItem"

def connection = new URL(apiUrl).openConnection()
connection.setRequestMethod("GET")
connection.setRequestProperty("Authorization", "Bearer " + accessToken)
connection.setRequestProperty("Accept", "application/json")

def responseCode = connection.getResponseCode()
if (responseCode != 200) {
    flowFile = session.putAttribute(flowFile, "error", "Graph API call failed with status ${responseCode}")
    session.transfer(flowFile, REL_FAILURE)
    return
}

def json = new JsonSlurper().parse(connection.getInputStream())

def driveId = json.parentReference?.driveId
def itemId = json.id
def fileName = json.name

flowFile = session.putAttribute(flowFile, "driveId", driveId ?: "")
flowFile = session.putAttribute(flowFile, "itemId", itemId ?: "")
flowFile = session.putAttribute(flowFile, "filename", fileName ?: "")

session.transfer(flowFile, REL_SUCCESS)

