import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import java.io.InputStream
import java.io.OutputStream
import java.io.IOException
import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.OutputStreamCallback

def flowFile = session.get()
if (!flowFile) return

try {
    def content = ''

    // Read JSON input from FlowFile content
    session.read(flowFile, new InputStreamCallback() {
        @Override
        void process(InputStream inputStream) throws IOException {
            content = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
        }
    })

    if (!content?.trim()) throw new IllegalArgumentException("FlowFile content is empty")

    def parsed = new JsonSlurper().parseText(content.trim())
    if (!(parsed instanceof List)) {
        parsed = [parsed]  // Convert to list if single JSON object
    }

    parsed.eachWithIndex { doc, idx ->
        def name = doc['name']?.toString()?.replaceAll("\\s+", "_") ?: "unknown"
        def empId = doc['employee_id']?.toString() ?: idx.toString()
        def filename = "${empId}_${name}.txt"
        def outputJson = JsonOutput.toJson([
            filename: filename,
            text: doc.toString()
        ])

        def ff = session.create(flowFile)
        session.write(ff, new OutputStreamCallback() {
            @Override
            void process(OutputStream out) throws IOException {
                out.write(JsonOutput.prettyPrint(outputJson).getBytes(StandardCharsets.UTF_8))
            }
        })

        session.transfer(ff, REL_SUCCESS)
    }

    session.remove(flowFile)

} catch (Exception e) {
    log.error("ExecuteScript Error: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}
