import org.apache.nifi.processor.io.StreamCallback
import java.nio.charset.StandardCharsets
import groovy.json.JsonOutput
import java.io.InputStreamReader
import java.io.BufferedReader

flowFile = session.get()
if (!flowFile) return

// Variable to store the input text
def inputText

// Use session.write to read the input and transform the flow file content
flowFile = session.write(flowFile, { inputStream, outputStream ->

    def reader = new BufferedReader(new InputStreamReader(inputStream, StandardCharsets.UTF_8))
    inputText = reader.readLines().join("\n").trim()

    // Expecting a JSON-like array: ["a@example.com", "b@example.com"]
    if (!inputText.startsWith("[")) {
        throw new RuntimeException("Expected array format in input")
    }

    def emailList = inputText
        .replaceAll(/[\[\]\s]/, '')
        .split(",")
        .collect { it.trim().replaceAll(/^"|"$/, '') }
        .findAll { it }

    def mongoQuery = [ "email": [ "\$in": emailList ] ]
    def queryJson = JsonOutput.toJson(mongoQuery)

    outputStream.write(queryJson.getBytes(StandardCharsets.UTF_8))

} as StreamCallback)

// After session.write completes, set the flow file attribute
flowFile = session.putAttribute(flowFile, 'originalEmailList', inputText)

session.transfer(flowFile, REL_SUCCESS)

import groovy.json.JsonSlurper
import groovy.json.JsonOutput

// Get the incoming flow file
def flowFile = session.get()
if (!flowFile) return

// Read the parsed resumes from the flow file content
def inputStream = session.read(flowFile)
def resumes = new JsonSlurper().parse(inputStream)
inputStream.close()

// Get the query from the chatInput attribute
def query = flowFile.getAttribute('chatInput')

// Construct the LLM context payload with a system prompt
def llmPayload = [
    messages: [
        [
            role: "system",
            content: "You are a helpful assistant that summarizes candidate resumes based on a job query. Quickly identify and return the top matching candidates in 2–3 concise lines each, focusing only on the most relevant skills and experience."
        ],
        [
            role: "user",
            content: JsonOutput.toJson([
                query: query,
                resumes: resumes
            ])
        ]
    ]
]

// Convert the payload to JSON string
def jsonOutput = JsonOutput.toJson(llmPayload)

// Write the new JSON payload to the flow file
flowFile = session.write(flowFile, { outputStream ->
    outputStream.write(jsonOutput.getBytes('UTF-8'))
} as OutputStreamCallback)

// Transfer the flow file to the success relationship
session.transfer(flowFile, REL_SUCCESS)

import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

flowFile = session.write(flowFile, { inputStream, outputStream ->
    def text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    def json = new JsonSlurper().parseText(text)

    // Extract the content string
    def raw = json?.choices?.getAt(0)?.message?.content ?: ""

    // Remove ```json, ```, whitespace
    def cleaned = raw.replaceAll("(?s)```(?:json)?\\s*", "").replaceAll("\\s*```", "").trim()

    // Write cleaned JSON array (e.g., ["Python", "Django"])
    outputStream.write(cleaned.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

