    def inputText = IOUtils.toString(inputStream, StandardCharsets.UTF_8).trim()

    // Parse input, which should be a JSON array of strings
    def jsonSlurper = new JsonSlurper()
    def keywords = jsonSlurper.parseText(inputText)

    // Construct MongoDB query
    def queryMap = [
        skills: [ '$in': keywords ]
    ]

    // Write query as FlowFile content
    def outputText = JsonOutput.toJson(queryMap)
    outputStream.write(outputText.getBytes(StandardCharsets.UTF_8))
} as StreamCallback)

session.transfer(flowFile, REL_SUCCESS)

