import org.apache.pdfbox.pdmodel.PDDocument
import org.apache.pdfbox.text.PDFTextStripper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets

// Get the incoming FlowFile
def flowFile = session.get()
if (!flowFile) return

try {
    def extractedText = new StringBuilder()
    def metadata = [:]

    session.read(flowFile, { inputStream ->
        PDDocument document = null
        try {
            document = PDDocument.load(inputStream)
            PDFTextStripper stripper = new PDFTextStripper()
            extractedText.append(stripper.getText(document))
            metadata.pageCount = document.getNumberOfPages()
        } finally {
            if (document != null) {
                document.close()
            }
        }
    } as InputStreamCallback)

    // Prepare JSON output
    def jsonMap = [
        filename : flowFile.getAttribute("filename"),
        pageCount: metadata.pageCount ?: 0,
        text     : extractedText.toString().trim()
    ]
    def jsonString = JsonOutput.prettyPrint(JsonOutput.toJson(jsonMap))

    // Write JSON back to flowfile
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(jsonString.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)

    flowFile = session.putAttribute(flowFile, "mime.type", "application/json")
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Failed to parse PDF and convert to JSON", e)
    session.transfer(flowFile, REL_FAILURE)
}

