import org.apache.nifi.processor.io.*
import org.apache.nifi.processor.*
import org.apache.nifi.flowfile.*
import groovy.json.*

// Get the flowfile from the session
flowFile = session.get()
if (!flowFile) return

// Variable to store the username
def username = null

// Modify the flowfile content
flowFile = session.write(flowFile, { inputStream, outputStream ->
    // Parse the input JSON
    def inputJson = new JsonSlurper().parse(inputStream)

    // Extract the linkedin_url field
    def linkedInUrl = inputJson?.linkedin_url

    // Extract the username if the URL contains "linkedin.com/in/"
    if (linkedInUrl?.contains("linkedin.com/in/")) {
        username = linkedInUrl.split("linkedin.com/in/")[1]
        username = username.split("[/?]")[0]  // Clean trailing slashes or parameters
    }

    // Add linkedin_username to the JSON
    inputJson["linkedin_username"] = username ?: ""

    // Write the modified JSON back to the flowfile
    outputStream.write(JsonOutput.toJson(inputJson).getBytes("UTF-8"))
} as StreamCallback)

// Set linkedin_username as an attribute
flowFile = session.putAttribute(flowFile, "linkedin_username", username ?: "")

// Add a status attribute
flowFile = session.putAttribute(flowFile, "script.status", "linkedin username extracted")

// Add error handling for empty username
if (!username) {
    flowFile = session.putAttribute(flowFile, "script.error", "Failed to extract linkedin_username")
    session.transfer(flowFile, REL_FAILURE)
} else {
    session.transfer(flowFile, REL_SUCCESS)
}
