// Import necessary NiFi classes
import org.apache.nifi.flowfile.attributes.CoreAttributes
import org.apache.nifi.processor.Relationship // <--- IMPORTANT: Import Relationship class
import java.nio.file.Paths
import java.nio.file.Files
import java.nio.file.DirectoryStream

// Define Relationships for NiFi 2.x - these are discovered by the processor upon startup
def REL_SUCCESS = new Relationship.Builder().name("success").description("Successfully processed FlowFile").build()
def REL_FAILURE = new Relationship.Builder().name("failure").description("Failed to process FlowFile").build()
def REL_NO_METADATA_FOUND = new Relationship.Builder().name("no.metadata.found").description("No matching metadata JSON file found").build()


// 'session', 'context', and 'log' are directly available in NiFi 2.x ExecuteScript
def flowFile = session.get()

if (flowFile != null) {
    def imageDirectoryPath = flowFile.getAttribute(CoreAttributes.ABSOLUTE_PATH.key())
    def imageFilename = flowFile.getAttribute(CoreAttributes.FILENAME.key()) // e.g., "image.jpeg"

    // Extract the base name of the image (without its *last* extension)
    // Example: "image.jpeg" -> "image"
    // Example: "image.tar.gz" -> "image.tar"
    def baseImageFilenameNoExt = ""
    def lastDotIndex = imageFilename.lastIndexOf('.')
    if (lastDotIndex != -1) {
        baseImageFilenameNoExt = imageFilename.substring(0, lastDotIndex)
    } else {
        baseImageFilenameNoExt = imageFilename // No extension, use full filename as base
    }

    def metadataFound = false
    def metadataFilePath = null

    try {
        def directory = Paths.get(imageDirectoryPath)

        // Step 1: Broad search for any JSON file starting with the image's base name
        // This cast a wide net: e.g., for "image.jpeg", it will find "image.jpe.json", "image.jpg.json", "image.json", etc.
        Files.newDirectoryStream(directory, "${baseImageFilenameNoExt}*.json").each { path ->
            def currentJsonFilename = path.getFileName().toString()

            // Step 2: Refine the match with specific rules
            // Rule A: Matches if the JSON filename starts exactly with the full image filename
            //         (e.g., "cows.jpg.json", "cows.jpg.supplemental-metadata.json" for "cows.jpg")
            if (currentJsonFilename.startsWith(imageFilename)) {
                metadataFilePath = path.toAbsolutePath().toString()
                metadataFound = true
                log.info("Rule A: Found matching metadata file: ${metadataFilePath} for image: ${imageFilename}")
                return // Break from each loop
            }

            // Rule B: Matches if the JSON filename starts with the image's base name (no extension)
            //         AND has a different extension-like part before '.json'.
            //         (e.g., "sumatran-tiger.jpe.json" for "sumatran-tiger.jpeg")
            //         This handles cases where the metadata file's extension differs from the image's.
            if (currentJsonFilename.startsWith(baseImageFilenameNoExt) &&
                currentJsonFilename.length() > baseImageFilenameNoExt.length() && // Ensure there's content after base name
                currentJsonFilename.substring(baseImageFilenameNoExt.length()).contains(".") // Ensure that content includes a dot (like an extension)
               ) {
                // Further check to make sure it's specifically for this image and not another file with similar prefix
                // This is the tricky part. For Google Takeout, the filename usually matches exactly or with a '.ext.json' suffix.
                // Given "sumatran-tiger-tiger-big-cat-stripes-46251.jpe.json" for "sumatran-tiger-tiger-big-cat-stripes-46251.jpeg",
                // This condition should match.
                metadataFilePath = path.toAbsolutePath().toString()
                metadataFound = true
                log.info("Rule B: Found matching metadata file: ${metadataFilePath} for image: ${imageFilename}")
                return // Break from each loop
            }
        }

        if (metadataFound) {
            def metadataContent = new String(Files.readAllBytes(Paths.get(metadataFilePath)), 'UTF-8')
            flowFile = session.putAttribute(flowFile, 'google.metadata', metadataContent)
            session.transfer(flowFile, REL_SUCCESS)
        } else {
            log.warn("No matching metadata JSON file found for image: '${imageFilename}' in directory: '${imageDirectoryPath}'. Routing to 'NO_METADATA_FOUND' relationship.")
            session.transfer(flowFile, REL_NO_METADATA_FOUND)
        }

    } catch (Exception e) {
        log.error("Error processing metadata for '${imageFilename}': ${e.message}", e)
        session.transfer(flowFile, REL_FAILURE)
    }
} else {
    log.warn("No FlowFile available to process.")
}

// Import necessary classes
import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.OutputStreamCallback // Ensure this is imported
import org.apache.nifi.processor.Relationship
import java.nio.charset.StandardCharsets
import groovy.json.JsonOutput // For converting Map to JSON

// Define Relationships (already defined, but keep for completeness)
def REL_SUCCESS = new Relationship.Builder().name("success").description("Successfully processed FlowFile").build()
def REL_FAILURE = new Relationship.Builder().name("failure").description("Failed to process FlowFile").build()


def flowFile = session.get()
if (flowFile != null) {
    try {
        // Read the Base64 image content from the FlowFile
        def base64ImageContent = new StringBuilder()
        session.read(flowFile, { inputStream ->
            base64ImageContent.append(new String(inputStream.readAllBytes(), StandardCharsets.UTF_8))
        } as InputStreamCallback)

        // Step 1: Create a standard Groovy Map representing the entire JSON structure
        def llmPayloadMap = [
            model: "gpt-4o", // Or your preferred multimodal model
            messages: [
                [
                    role: "user",
                    content: [
                        [
                            type: "text",
                            text: "Describe this image for a photo search engine. Be descriptive but concise. Mention people, animals, objects, colors, and the general setting."
                        ],
                        [
                            type: "image_url",
                            image_url: [
                                url: "data:image/jpeg;base64,${base64ImageContent}" // Embed Base64 content
                            ]
                        ]
                    ]
                ]
            ],
            max_tokens: 150
        ]

        // Step 2: Convert the Groovy Map directly to a JSON string
        def jsonString = JsonOutput.toJson(llmPayloadMap)
        // Optionally pretty print it for readability in logs/debug, not strictly needed for API calls
        jsonString = JsonOutput.prettyPrint(jsonString)


        // Write the new JSON payload back to the FlowFile content
        flowFile = session.write(flowFile, { outputStream ->
            outputStream.write(jsonString.getBytes(StandardCharsets.UTF_8))
        } as OutputStreamCallback)

        // Update FlowFile mime.type attribute for downstream processors
        flowFile = session.putAttribute(flowFile, "mime.type", "application/json")

        session.transfer(flowFile, REL_SUCCESS)

    } catch (Exception e) {
        log.error("Error building LLM JSON payload for FlowFile ${flowFile.getId()}: ${e.message}", e)
        session.transfer(flowFile, REL_FAILURE)
    }
} else {
    log.warn("No FlowFile available to process in LLM Payload Builder script.")
}

import groovy.json.JsonOutput
import org.apache.nifi.flowfile.FlowFile
import java.nio.charset.StandardCharsets

def ff = session.get()
if (!ff) return

try {
    def description = ff.getAttribute('llm.summary')
    // The model (deployment id) is part of the URL for Azure OpenAI embeddings.
    // The payload is just {"input": "text_to_embed"}
    def payloadMap = [ input: description ]
    def payloadJson = JsonOutput.toJson(payloadMap)

    ff = session.write(ff, { outputStream ->
        outputStream.write(payloadJson.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)
    session.transfer(ff, REL_SUCCESS)
} catch (e) {
    log.error("Error preparing Embedding payload for ${ff}: " + e.getMessage(), e)
    session.transfer(ff, REL_FAILURE)
}

import groovy.json.JsonOutput
import groovy.json.JsonSlurper
import org.apache.nifi.processor.io.OutputStreamCallback
import org.apache.nifi.processor.Relationship
import java.nio.charset.StandardCharsets
import java.util.UUID
import java.nio.file.Paths

def REL_SUCCESS = new Relationship.Builder().name("success").description("Success").build()
def REL_FAILURE = new Relationship.Builder().name("failure").description("Failure").build()

def flowFile = session.get()
if (!flowFile) return

try {
    def jsonSlurper = new JsonSlurper()
    def path = flowFile.getAttribute('path')
    def filename = flowFile.getAttribute('filename')
    def imagePath = Paths.get(path, filename).toString()
    def summary = flowFile.getAttribute('llm.summary') ?: ""
    def embedding = jsonSlurper.parseText(flowFile.getAttribute('extracted.embedding'))
    if (!(embedding instanceof List)) throw new Exception("Embedding is not a valid list")

    def metadata = flowFile.getAttribute('google.metadata') ? jsonSlurper.parseText(flowFile.getAttribute('google.metadata')) : [:]

    // Derive basename (filename without extension)
    def basename = filename.contains(".") ? filename[0..filename.lastIndexOf('.') - 1] : filename

    // Normalize persons as text array (e.g., ["rohit sharma"])
    def personsRaw = metadata?.people ?: metadata?.persons ?: []
    def persons = personsRaw.collect {
        if (it instanceof Map && it.name) return it.name.toLowerCase()
        if (it instanceof String) return it.toLowerCase()
        return null
    }.findAll { it }

    def payload = [
        class     : "image_search",  // Weaviate class name
        id        : UUID.randomUUID().toString(),
        properties: [
            image_path      : imagePath,
            summary         : summary,
            title           : metadata?.title ?: null,
            description     : metadata?.description ?: null,
            imageViews      : metadata?.imageViews?.toInteger() ?: null,
            timestamp       : metadata?.photoTakenTime?.timestamp?.toLong() ?: null,
            formatted_time  : metadata?.photoTakenTime?.formatted ?: null,
            url             : metadata?.url ?: null,
            latitude        : metadata?.geoData?.latitude ?: null,
            longitude       : metadata?.geoData?.longitude ?: null,
            altitude        : metadata?.geoData?.altitude ?: null,
            appName         : metadata?.googlePhotosOrigin?.thirdPartyApp?.appName
                              ?: metadata?.appSource?.androidPackageName ?: null,
            deviceType      : metadata?.googlePhotosOrigin?.mobileUpload?.deviceType ?: null,
            localFolderName : metadata?.googlePhotosOrigin?.mobileUpload?.deviceFolder?.localFolderName ?: null,
            persons         : persons,
            basename        : basename
        ],
        vector: embedding
    ]

    def jsonString = JsonOutput.prettyPrint(JsonOutput.toJson(payload))

    flowFile = session.write(flowFile, { out -> out.write(jsonString.getBytes(StandardCharsets.UTF_8)) } as OutputStreamCallback)
    flowFile = session.putAttribute(flowFile, "mime.type", "application/json")
    session.transfer(flowFile, REL_SUCCESS)
    log.info(" Weaviate upsert payload prepared for FlowFile ${flowFile.getId()}.")

} catch (Exception e) {
    log.error(" Failed to prepare Weaviate payload: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}
