import groovy.json.JsonSlurper
import groovy.json.JsonBuilder
import java.nio.charset.StandardCharsets
import java.time.LocalDate
import org.apache.nifi.processor.io.StreamCallback

def flowFile = session.get()
if (!flowFile) return

try {
    def invoiceJson = new StringBuilder()

    // Read content from flowFile
    flowFile = session.write(flowFile, { inputStream, outputStream ->
        invoiceJson.append(inputStream.getText(StandardCharsets.UTF_8.name()))
    } as StreamCallback)

    // Parse JSON content
    def slurper = new JsonSlurper()
    def invoice = slurper.parseText(invoiceJson.toString())

    def today = LocalDate.now().toString()

    // Create array of values
    def jsonArray = [
        invoice.VendorName,
        today,
        invoice.LineItemDescription,
        invoice.Date,
        invoice.InvoiceAmt,
        invoice.InvoiceNumber,
        invoice.GSTIN,
        invoice.GST,
        invoice.VendorAddress,
        "",         // TDS
        "",			// TDS Amount
		"",         // Amount Payable
        "Machine"   // Created By
    ]

    def jsonBuilder = new JsonBuilder(jsonArray)
    def jsonArrayString = jsonBuilder.toString()

    // Write new content to flowFile
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(jsonArrayString.getBytes(StandardCharsets.UTF_8))
    } as OutputStreamCallback)

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Failed to process FlowFile", e)
    session.transfer(flowFile, REL_FAILURE)
}

